package com.alex.blog.model;import com.alex.blog.base.BaseModel;import java.util.Date;/** * 文章分类表 Model * * @author alex * @date 2019-09-09 22:24:02 */public class BlogArticleCategory extends BaseModel {		/** 	* 自增id	*/	private Integer id;	/** 	* 分类名称	*/	private String categoryName;	/** 	* 分类code	*/	private String categoryCode;	/** 	* 父级分类id：顶级为0	*/	private Integer parentId;	/** 	* 分类图标	*/	private String icon;	/** 	* 分类排序，数字越大越在前	*/	private Integer orderSort;	/** 	* 创建时间	*/	private Date createTime;	/** 	* 更新时间	*/	private Date updateTime;		public Integer getId() {	    return this.id;	}	public void setId(Integer id) {	    this.id=id;	}	public String getCategoryName() {	    return this.categoryName;	}	public void setCategoryName(String categoryName) {	    this.categoryName=categoryName;	}	public String getCategoryCode() {	    return this.categoryCode;	}	public void setCategoryCode(String categoryCode) {	    this.categoryCode=categoryCode;	}	public Integer getParentId() {	    return this.parentId;	}	public void setParentId(Integer parentId) {	    this.parentId=parentId;	}	public String getIcon() {	    return this.icon;	}	public void setIcon(String icon) {	    this.icon=icon;	}	public Integer getOrderSort() {	    return this.orderSort;	}	public void setOrderSort(Integer orderSort) {	    this.orderSort=orderSort;	}	public Date getCreateTime() {	    return this.createTime;	}	public void setCreateTime(Date createTime) {	    this.createTime=createTime;	}	public Date getUpdateTime() {	    return this.updateTime;	}	public void setUpdateTime(Date updateTime) {	    this.updateTime=updateTime;	}}